contracts/BlocPol.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract BlocPol {
    // --- Events ---
    event CandidateRegistered(uint indexed candidateId, string name, string ipfsHash);
    event VotingSessionStarted(uint startTime, uint endTime);
    event VotingSessionStopped(uint stopTime);
    event VoteCast(address indexed voter, uint indexed candidateId, bytes32 voteHash, bytes32 txHash);

    // --- Structs ---
    struct Candidate {
        uint id;
        string name;
        string ipfsHash; // Optional IPFS hash for off-chain profile
        uint voteCount;
    }

    // --- State Variables ---
    address public admin;
    uint public deploymentTimestamp;
    bool public votingActive;
    uint public votingStartTime;
    uint public votingEndTime;
    uint private nextCandidateId;

    mapping(uint => Candidate) private candidates;
    uint[] private candidateIds;
    mapping(address => bool) private hasVoted;
    mapping(address => uint) private voterToCandidate;
    mapping(address => bytes32) private voterToVoteHash;
    mapping(address => bytes32) private voterToTxHash;

    // --- Modifiers ---
    modifier onlyAdmin() {
        require(msg.sender == admin, "Only admin can perform this action");
        _;
    }
    modifier onlyDuringVoting() {
        require(votingActive, "Voting is not active");
        require(block.timestamp >= votingStartTime && block.timestamp <= votingEndTime, "Voting not in allowed period");
        _;
    }
    modifier onlyOnce() {
        require(!hasVoted[msg.sender], "You have already voted");
        _;
    }

    // --- Constructor ---
    constructor() {
        admin = msg.sender;
        deploymentTimestamp = block.timestamp;
    }

    // --- Admin Functions ---
    function registerCandidate(string calldata name, string calldata ipfsHash) external onlyAdmin {
        uint candidateId = nextCandidateId++;
        candidates[candidateId] = Candidate(candidateId, name, ipfsHash, 0);
        candidateIds.push(candidateId);
        emit CandidateRegistered(candidateId, name, ipfsHash);
    }

    function startVotingSession(uint durationSeconds) external onlyAdmin {
        require(!votingActive, "Voting already active");
        votingActive = true;
        votingStartTime = block.timestamp;
        votingEndTime = block.timestamp + durationSeconds;
        emit VotingSessionStarted(votingStartTime, votingEndTime);
    }

    function stopVotingSession() external onlyAdmin {
        require(votingActive, "Voting not active");
        votingActive = false;
        votingEndTime = block.timestamp;
        emit VotingSessionStopped(votingEndTime);
    }

    // --- Voting Functions ---
    function vote(uint candidateId) external onlyDuringVoting onlyOnce {
        require(candidates[candidateId].id == candidateId, "Invalid candidate");
        candidates[candidateId].voteCount++;
        hasVoted[msg.sender] = true;
        voterToCandidate[msg.sender] = candidateId;
        bytes32 voteHash = keccak256(abi.encodePacked(msg.sender, candidateId, block.timestamp));
        voterToVoteHash[msg.sender] = voteHash;
        bytes32 txHash = keccak256(abi.encodePacked(msg.sender, candidateId, block.number, blockhash(block.number - 1)));
        voterToTxHash[msg.sender] = txHash;
        emit VoteCast(msg.sender, candidateId, voteHash, txHash);
    }

    // --- View Functions ---
    function getCandidates() external view returns (Candidate[] memory) {
        Candidate[] memory list = new Candidate[](candidateIds.length);
        for (uint i = 0; i < candidateIds.length; i++) {
            list[i] = candidates[candidateIds[i]];
        }
        return list;
    }

    function getCandidate(uint candidateId) external view returns (Candidate memory) {
        return candidates[candidateId];
    }

    function getTotalVotes(uint candidateId) external view returns (uint) {
        return candidates[candidateId].voteCount;
    }

    function isVotingActive() external view returns (bool) {
        return votingActive && block.timestamp >= votingStartTime && block.timestamp <= votingEndTime;
    }

    function hasAddressVoted(address addr) external view returns (bool) {
        return hasVoted[addr];
    }

    function getVoteHash(address voter) external view returns (bytes32) {
        require(hasVoted[voter], "No vote found for this address");
        return voterToVoteHash[voter];
    }

    function getVoteTxHash(address voter) external view returns (bytes32) {
        require(hasVoted[voter], "No vote found for this address");
        return voterToTxHash[voter];
    }

    function getDeploymentTimestamp() external view returns (uint) {
        return deploymentTimestamp;
    }

    function getVotedCandidate(address voter) external view returns (uint) {
        require(hasVoted[voter], "No vote found for this address");
        return voterToCandidate[voter];
    }

    // --- Transparent Vote Viewing (anonymous) ---
    function getAllVotes() external view returns (uint[] memory) {
        uint[] memory votes = new uint[](candidateIds.length);
        for (uint i = 0; i < candidateIds.length; i++) {
            votes[i] = candidates[candidateIds[i]].voteCount;
        }
        return votes;
    }
}

contracts/BlocPolAdvanced.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "./libraries/CryptographicUtils.sol";
import "./ZKProofVerifier.sol";
import "./LiquidDemocracy.sol";
import "./VoteMixing.sol";

/**
 * @title BlocPolAdvanced
 * @dev Advanced blockchain voting system with ZK-proofs, liquid democracy, and vote mixing
 * @notice Comprehensive voting system with privacy, scalability, and transparency features
 */
contract BlocPolAdvanced is Ownable, ReentrancyGuard, Pausable {
    using CryptographicUtils for *;

    // --- Structs ---
    struct Candidate {
        uint256 id;
        string name;
        string ipfsHash;
        uint256 voteCount;
        uint256 delegatedVoteCount;
        bool isActive;
        uint256 registrationTime;
    }

    struct AdvancedVote {
        address voter;
        uint256 candidateId;
        uint256[] rankedChoices;      // For ranked choice voting
        bytes32 commitmentHash;       // For commitment scheme
        bytes32 zkProofHash;          // For ZK-proof verification
        bytes32 mixedVoteHash;        // For vote mixing
        uint256 votingPower;          // For liquid democracy
        uint256 timestamp;
        bool isRevealed;
        VoteType voteType;
    }

    struct VotingSession {
        uint256 sessionId;
        uint256 startTime;
        uint256 endTime;
        uint256 revealTime;
        bool isActive;
        bool isRevealed;
        uint256 totalVotes;
        uint256 totalVotingPower;
        VotingMode mode;
        bytes32 merkleRoot;
    }

    struct AuditTrail {
        bytes32 actionHash;
        address actor;
        string action;
        uint256 timestamp;
        bytes32[] proof;
    }

    enum VoteType {
        DIRECT,         // Direct vote
        DELEGATED,      // Delegated vote
        MIXED,          // Mixed vote
        ZK_PROOF        // Zero-knowledge proof vote
    }

    enum VotingMode {
        SIMPLE_MAJORITY,
        RANKED_CHOICE,
        LIQUID_DEMOCRACY,
        MIXED_ANONYMOUS
    }

    // --- State Variables ---
    mapping(uint256 => Candidate) private candidates;
    mapping(uint256 => AdvancedVote) private votes;
    mapping(uint256 => VotingSession) private votingSessions;
    mapping(address => mapping(uint256 => bool)) private hasVoted;
    mapping(address => uint256[]) private voterVotes;
    mapping(bytes32 => AuditTrail) private auditTrails;
    
    uint256 private candidateCounter;
    uint256 private voteCounter;
    uint256 private sessionCounter;
    uint256 private auditCounter;
    
    // Contract references
    ZKProofVerifier public zkProofVerifier;
    LiquidDemocracy public liquidDemocracy;
    VoteMixing public voteMixing;
    
    // Configuration
    uint256 public minVotingPower;
    uint256 public maxVotingPower;
    uint256 public commitmentPeriod;
    uint256 public revealPeriod;
    bool public zkProofsEnabled;
    bool public liquidDemocracyEnabled;
    bool public voteMixingEnabled;
    
    // --- Events ---
    event CandidateRegistered(uint256 indexed candidateId, string name, string ipfsHash, uint256 timestamp);
    event VotingSessionCreated(uint256 indexed sessionId, VotingMode mode, uint256 startTime, uint256 endTime);
    event VoteCast(address indexed voter, uint256 indexed candidateId, VoteType voteType, uint256 timestamp);
    event VoteRevealed(address indexed voter, uint256 indexed candidateId, uint256 timestamp);
    event VotingSessionEnded(uint256 indexed sessionId, uint256 totalVotes, uint256 timestamp);
    event AuditLogCreated(bytes32 indexed actionHash, address indexed actor, string action, uint256 timestamp);
    event ZKProofVerified(bytes32 indexed proofHash, bool isValid, uint256 timestamp);
    event DelegationCreated(address indexed from, address indexed to, uint256 power, uint256 timestamp);
    event VoteMixed(bytes32 indexed originalVote, bytes32 indexed mixedVote, uint256 timestamp);

    // --- Modifiers ---
    modifier onlyActiveSession(uint256 sessionId) {
        require(votingSessions[sessionId].isActive, "Session not active");
        _;
    }

    modifier onlyValidCandidate(uint256 candidateId) {
        require(candidates[candidateId].isActive, "Invalid candidate");
        _;
    }

    modifier onlyValidVotingPower(uint256 power) {
        require(power >= minVotingPower && power <= maxVotingPower, "Invalid voting power");
        _;
    }

    // --- Constructor ---
    constructor(
        address _zkProofVerifier,
        address _liquidDemocracy,
        address _voteMixing,
        uint256 _minVotingPower,
        uint256 _maxVotingPower,
        uint256 _commitmentPeriod,
        uint256 _revealPeriod
    ) Ownable(msg.sender) {
        zkProofVerifier = ZKProofVerifier(_zkProofVerifier);
        liquidDemocracy = LiquidDemocracy(_liquidDemocracy);
        voteMixing = VoteMixing(_voteMixing);
        
        minVotingPower = _minVotingPower;
        maxVotingPower = _maxVotingPower;
        commitmentPeriod = _commitmentPeriod;
        revealPeriod = _revealPeriod;
        
        candidateCounter = 0;
        voteCounter = 0;
        sessionCounter = 0;
        auditCounter = 0;
        
        zkProofsEnabled = true;
        liquidDemocracyEnabled = true;
        voteMixingEnabled = true;
    }

    // --- Core Functions ---

    /**
     * @dev Registers a new candidate
     * @param name Candidate name
     * @param ipfsHash IPFS hash for candidate profile
     * @return candidateId The ID of the registered candidate
     */
    function registerCandidate(
        string calldata name,
        string calldata ipfsHash
    ) external onlyOwner returns (uint256 candidateId) {
        candidateId = candidateCounter++;
        
        candidates[candidateId] = Candidate({
            id: candidateId,
            name: name,
            ipfsHash: ipfsHash,
            voteCount: 0,
            delegatedVoteCount: 0,
            isActive: true,
            registrationTime: block.timestamp
        });
        
        logAuditTrail("CANDIDATE_REGISTERED", abi.encodePacked(candidateId, name, ipfsHash));
        
        emit CandidateRegistered(candidateId, name, ipfsHash, block.timestamp);
        
        return candidateId;
    }

    /**
     * @dev Creates a new voting session
     * @param durationSeconds Duration of the voting session
     * @param mode Voting mode to use
     * @return sessionId The ID of the created session
     */
    function createVotingSession(
        uint256 durationSeconds,
        VotingMode mode
    ) external onlyOwner returns (uint256 sessionId) {
        sessionId = sessionCounter++;
        
        VotingSession memory newSession = VotingSession({
            sessionId: sessionId,
            startTime: block.timestamp,
            endTime: block.timestamp + durationSeconds,
            revealTime: block.timestamp + durationSeconds + revealPeriod,
            isActive: true,
            isRevealed: false,
            totalVotes: 0,
            totalVotingPower: 0,
            mode: mode,
            merkleRoot: bytes32(0)
        });
        
        votingSessions[sessionId] = newSession;
        
        logAuditTrail("VOTING_SESSION_CREATED", abi.encodePacked(sessionId, mode, durationSeconds));
        
        emit VotingSessionCreated(sessionId, mode, newSession.startTime, newSession.endTime);
        
        return sessionId;
    }

    /**
     * @dev Casts a vote with commitment scheme
     * @param sessionId The voting session ID
     * @param candidateId The candidate to vote for
     * @param commitmentHash Commitment hash of the vote
     * @param votingPower The voting power to use
     * @return voteId The ID of the cast vote
     */
    function castVote(
        uint256 sessionId,
        uint256 candidateId,
        bytes32 commitmentHash,
        uint256 votingPower
    ) external 
        onlyActiveSession(sessionId)
        onlyValidCandidate(candidateId)
        onlyValidVotingPower(votingPower)
        returns (uint256 voteId) 
    {
        require(!hasVoted[msg.sender][sessionId], "Already voted in this session");
        require(block.timestamp <= votingSessions[sessionId].endTime, "Voting period ended");
        
        voteId = voteCounter++;
        
        AdvancedVote memory newVote = AdvancedVote({
            voter: msg.sender,
            candidateId: candidateId,
            rankedChoices: new uint256[](0),
            commitmentHash: commitmentHash,
            zkProofHash: bytes32(0),
            mixedVoteHash: bytes32(0),
            votingPower: votingPower,
            timestamp: block.timestamp,
            isRevealed: false,
            voteType: VoteType.DIRECT
        });
        
        votes[voteId] = newVote;
        hasVoted[msg.sender][sessionId] = true;
        voterVotes[msg.sender].push(voteId);
        
        // Update session statistics
        votingSessions[sessionId].totalVotes++;
        votingSessions[sessionId].totalVotingPower += votingPower;
        
        logAuditTrail("VOTE_CAST", abi.encodePacked(voteId, sessionId, candidateId, votingPower));
        
        emit VoteCast(msg.sender, candidateId, VoteType.DIRECT, block.timestamp);
        
        return voteId;
    }

    /**
     * @dev Casts a vote with ZK-proof verification
     * @param sessionId The voting session ID
     * @param candidateId The candidate to vote for
     * @param zkProofHash ZK-proof hash
     * @param votingPower The voting power to use
     * @return voteId The ID of the cast vote
     */
    function castZKVote(
        uint256 sessionId,
        uint256 candidateId,
        bytes32 zkProofHash,
        uint256 votingPower
    ) external 
        onlyActiveSession(sessionId)
        onlyValidCandidate(candidateId)
        onlyValidVotingPower(votingPower)
        returns (uint256 voteId) 
    {
        require(zkProofsEnabled, "ZK-proofs disabled");
        require(!hasVoted[msg.sender][sessionId], "Already voted in this session");
        
        voteId = voteCounter++;
        
        AdvancedVote memory newVote = AdvancedVote({
            voter: msg.sender,
            candidateId: candidateId,
            rankedChoices: new uint256[](0),
            commitmentHash: bytes32(0),
            zkProofHash: zkProofHash,
            mixedVoteHash: bytes32(0),
            votingPower: votingPower,
            timestamp: block.timestamp,
            isRevealed: false,
            voteType: VoteType.ZK_PROOF
        });
        
        votes[voteId] = newVote;
        hasVoted[msg.sender][sessionId] = true;
        voterVotes[msg.sender].push(voteId);
        
        // Update session statistics
        votingSessions[sessionId].totalVotes++;
        votingSessions[sessionId].totalVotingPower += votingPower;
        
        logAuditTrail("ZK_VOTE_CAST", abi.encodePacked(voteId, sessionId, candidateId, zkProofHash));
        
        emit VoteCast(msg.sender, candidateId, VoteType.ZK_PROOF, block.timestamp);
        
        return voteId;
    }

    /**
     * @dev Casts a delegated vote using liquid democracy
     * @param sessionId The voting session ID
     * @param candidateId The candidate to vote for
     * @param votingPower The voting power to use
     * @return voteId The ID of the cast vote
     */
    function castDelegatedVote(
        uint256 sessionId,
        uint256 candidateId,
        uint256 votingPower
    ) external 
        onlyActiveSession(sessionId)
        onlyValidCandidate(candidateId)
        onlyValidVotingPower(votingPower)
        returns (uint256 voteId) 
    {
        require(liquidDemocracyEnabled, "Liquid democracy disabled");
        require(!hasVoted[msg.sender][sessionId], "Already voted in this session");
        
        // Verify voter has sufficient power in liquid democracy
        LiquidDemocracy.Voter memory voter = liquidDemocracy.getVoter(msg.sender);
        require(voter.totalPower >= votingPower, "Insufficient voting power");
        
        voteId = voteCounter++;
        
        AdvancedVote memory newVote = AdvancedVote({
            voter: msg.sender,
            candidateId: candidateId,
            rankedChoices: new uint256[](0),
            commitmentHash: bytes32(0),
            zkProofHash: bytes32(0),
            mixedVoteHash: bytes32(0),
            votingPower: votingPower,
            timestamp: block.timestamp,
            isRevealed: false,
            voteType: VoteType.DELEGATED
        });
        
        votes[voteId] = newVote;
        hasVoted[msg.sender][sessionId] = true;
        voterVotes[msg.sender].push(voteId);
        
        // Update session statistics
        votingSessions[sessionId].totalVotes++;
        votingSessions[sessionId].totalVotingPower += votingPower;
        
        logAuditTrail("DELEGATED_VOTE_CAST", abi.encodePacked(voteId, sessionId, candidateId, votingPower));
        
        emit VoteCast(msg.sender, candidateId, VoteType.DELEGATED, block.timestamp);
        
        return voteId;
    }

    /**
     * @dev Reveals a committed vote
     * @param voteId The vote ID to reveal
     * @param originalVote The original vote value
     * @param randomness The randomness used in commitment
     */
    function revealVote(
        uint256 voteId,
        bytes32 originalVote,
        bytes32 randomness
    ) external {
        AdvancedVote storage vote = votes[voteId];
        require(vote.voter == msg.sender, "Not your vote");
        require(!vote.isRevealed, "Vote already revealed");
        require(vote.voteType == VoteType.DIRECT, "Not a direct vote");
        
        // Verify commitment
        require(CryptographicUtils.verifyCommitment(originalVote, randomness, vote.commitmentHash), "Invalid commitment");
        
        vote.isRevealed = true;
        
        // Update candidate vote count
        candidates[vote.candidateId].voteCount += vote.votingPower;
        
        logAuditTrail("VOTE_REVEALED", abi.encodePacked(voteId, originalVote));
        
        emit VoteRevealed(msg.sender, vote.candidateId, block.timestamp);
    }

    /**
     * @dev Ends a voting session and calculates results
     * @param sessionId The session ID to end
     */
    function endVotingSession(uint256 sessionId) external onlyOwner {
        VotingSession storage session = votingSessions[sessionId];
        require(session.isActive, "Session not active");
        require(block.timestamp >= session.endTime, "Session not ended");
        
        session.isActive = false;
        
        // Create Merkle root from all votes
        bytes32[] memory voteHashes = new bytes32[](session.totalVotes);
        for (uint256 i = 0; i < session.totalVotes; i++) {
            voteHashes[i] = keccak256(abi.encodePacked(i, votes[i].voter, votes[i].candidateId));
        }
        session.merkleRoot = CryptographicUtils.createMerkleRootFromMemory(voteHashes);
        
        logAuditTrail("VOTING_SESSION_ENDED", abi.encodePacked(sessionId, session.totalVotes, session.totalVotingPower));
        
        emit VotingSessionEnded(sessionId, session.totalVotes, block.timestamp);
    }

    // --- View Functions ---

    /**
     * @dev Gets candidate information
     * @param candidateId The candidate ID
     * @return The candidate structure
     */
    function getCandidate(uint256 candidateId) external view returns (Candidate memory) {
        return candidates[candidateId];
    }

    /**
     * @dev Gets all candidates
     * @return Array of candidate structures
     */
    function getAllCandidates() external view returns (Candidate[] memory) {
        Candidate[] memory allCandidates = new Candidate[](candidateCounter);
        for (uint256 i = 0; i < candidateCounter; i++) {
            allCandidates[i] = candidates[i];
        }
        return allCandidates;
    }

    /**
     * @dev Gets vote information
     * @param voteId The vote ID
     * @return The vote structure
     */
    function getVote(uint256 voteId) external view returns (AdvancedVote memory) {
        return votes[voteId];
    }

    /**
     * @dev Gets voting session information
     * @param sessionId The session ID
     * @return The session structure
     */
    function getVotingSession(uint256 sessionId) external view returns (VotingSession memory) {
        return votingSessions[sessionId];
    }

    /**
     * @dev Gets all votes for a voter
     * @param voter The voter address
     * @return Array of vote IDs
     */
    function getVoterVotes(address voter) external view returns (uint256[] memory) {
        return voterVotes[voter];
    }

    /**
     * @dev Checks if a voter has voted in a session
     * @param voter The voter address
     * @param sessionId The session ID
     * @return Whether the voter has voted
     */
    function hasVoterVoted(address voter, uint256 sessionId) external view returns (bool) {
        return hasVoted[voter][sessionId];
    }

    /**
     * @dev Gets audit trail information
     * @param actionHash The action hash
     * @return The audit trail structure
     */
    function getAuditTrail(bytes32 actionHash) external view returns (AuditTrail memory) {
        return auditTrails[actionHash];
    }

    // --- Admin Functions ---

    /**
     * @dev Updates voting parameters
     * @param _minVotingPower New minimum voting power
     * @param _maxVotingPower New maximum voting power
     * @param _commitmentPeriod New commitment period
     * @param _revealPeriod New reveal period
     */
    function updateVotingParameters(
        uint256 _minVotingPower,
        uint256 _maxVotingPower,
        uint256 _commitmentPeriod,
        uint256 _revealPeriod
    ) external onlyOwner {
        minVotingPower = _minVotingPower;
        maxVotingPower = _maxVotingPower;
        commitmentPeriod = _commitmentPeriod;
        revealPeriod = _revealPeriod;
    }

    /**
     * @dev Enables or disables features
     * @param _zkProofsEnabled Whether ZK-proofs are enabled
     * @param _liquidDemocracyEnabled Whether liquid democracy is enabled
     * @param _voteMixingEnabled Whether vote mixing is enabled
     */
    function setFeatures(
        bool _zkProofsEnabled,
        bool _liquidDemocracyEnabled,
        bool _voteMixingEnabled
    ) external onlyOwner {
        zkProofsEnabled = _zkProofsEnabled;
        liquidDemocracyEnabled = _liquidDemocracyEnabled;
        voteMixingEnabled = _voteMixingEnabled;
    }

    /**
     * @dev Pauses the contract
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev Unpauses the contract
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    // --- Internal Functions ---

    /**
     * @dev Logs an audit trail entry
     * @param action The action performed
     * @param data Additional data
     */
    function logAuditTrail(string memory action, bytes memory data) internal {
        bytes32 actionHash = keccak256(abi.encodePacked(action, data, block.timestamp, auditCounter));
        
        AuditTrail memory trail = AuditTrail({
            actionHash: actionHash,
            actor: msg.sender,
            action: action,
            timestamp: block.timestamp,
            proof: new bytes32[](0)
        });
        
        auditTrails[actionHash] = trail;
        auditCounter++;
        
        emit AuditLogCreated(actionHash, msg.sender, action, block.timestamp);
    }

    /**
     * @dev Gets total vote count for a candidate
     * @param candidateId The candidate ID
     * @return Total vote count
     */
    function getCandidateTotalVotes(uint256 candidateId) external view returns (uint256) {
        return candidates[candidateId].voteCount + candidates[candidateId].delegatedVoteCount;
    }

    /**
     * @dev Gets total votes in a session
     * @param sessionId The session ID
     * @return totalVotes Total number of votes cast
     * @return totalVotingPower Total voting power used
     */
    function getSessionTotals(uint256 sessionId) external view returns (uint256 totalVotes, uint256 totalVotingPower) {
        VotingSession memory session = votingSessions[sessionId];
        return (session.totalVotes, session.totalVotingPower);
    }
}

contracts/libraries/CryptographicUtils.s.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/utils/cryptography/MerkleProof.sol";

/**
 * @title CryptographicUtils
 * @dev Advanced cryptographic utilities for secure voting
 * @notice Provides commitment schemes, threshold cryptography, and vote mixing
 */
library CryptographicUtils {
    using ECDSA for bytes32;

    // --- Structs ---
    struct Commitment {
        bytes32 commitmentHash;
        uint256 timestamp;
        bool revealed;
        bytes32 revealedValue;
    }

    struct ThresholdSignature {
        bytes32 messageHash;
        bytes[] signatures;
        uint256 threshold;
        uint256 totalSigners;
    }

    struct VoteMix {
        bytes32 originalVote;
        bytes32 mixedVote;
        uint256 mixIndex;
        bytes32 randomness;
    }

    // --- Events ---
    event CommitmentCreated(address indexed voter, bytes32 commitmentHash, uint256 timestamp);
    event CommitmentRevealed(address indexed voter, bytes32 originalValue, bytes32 commitmentHash);
    event ThresholdSignatureCreated(bytes32 indexed messageHash, uint256 threshold, uint256 totalSigners);
    event VoteMixed(bytes32 indexed originalVote, bytes32 mixedVote, uint256 mixIndex);

    // --- Commitment Scheme Functions ---
    
    /**
     * @dev Creates a commitment hash for vote hiding
     * @param vote The vote to commit
     * @param randomness Random value for commitment
     * @return commitmentHash The commitment hash
     */
    function createCommitment(bytes32 vote, bytes32 randomness) 
        internal 
        pure 
        returns (bytes32 commitmentHash) 
    {
        commitmentHash = keccak256(abi.encodePacked(vote, randomness));
        return commitmentHash;
    }

    /**
     * @dev Verifies a commitment reveal
     * @param vote The revealed vote
     * @param randomness The randomness used in commitment
     * @param commitmentHash The original commitment hash
     * @return isValid Whether the reveal is valid
     */
    function verifyCommitment(
        bytes32 vote, 
        bytes32 randomness, 
        bytes32 commitmentHash
    ) internal pure returns (bool isValid) {
        bytes32 computedHash = keccak256(abi.encodePacked(vote, randomness));
        return computedHash == commitmentHash;
    }

    // --- Threshold Cryptography Functions ---

    /**
     * @dev Creates a threshold signature verification
     * @param messageHash Hash of the message to verify
     * @param signatures Array of signatures
     * @param signers Array of signer addresses
     * @param threshold Minimum number of valid signatures required
     * @return isValid Whether threshold is met
     */
    function verifyThresholdSignature(
        bytes32 messageHash,
        bytes[] calldata signatures,
        address[] calldata signers,
        uint256 threshold
    ) internal pure returns (bool isValid) {
        require(signatures.length == signers.length, "Signature count mismatch");
        require(signatures.length >= threshold, "Insufficient signatures for threshold");
        
        uint256 validSignatures = 0;
        address[] memory recoveredSigners = new address[](signatures.length);
        
        for (uint256 i = 0; i < signatures.length; i++) {
            address recoveredSigner = messageHash.recover(signatures[i]);
            if (recoveredSigner == signers[i]) {
                recoveredSigners[validSignatures] = recoveredSigner;
                validSignatures++;
            }
        }
        
        // Check for duplicate signers
        for (uint256 i = 0; i < validSignatures; i++) {
            for (uint256 j = i + 1; j < validSignatures; j++) {
                require(recoveredSigners[i] != recoveredSigners[j], "Duplicate signer");
            }
        }
        
        return validSignatures >= threshold;
    }

    // --- Vote Mixing Functions ---

    /**
     * @dev Creates a mixed vote using Chaum mixing
     * @param originalVote The original vote
     * @param randomness Random value for mixing
     * @param mixIndex Index in the mixing round
     * @return mixedVote The mixed vote
     */
    function mixVote(
        bytes32 originalVote,
        bytes32 randomness,
        uint256 mixIndex
    ) internal pure returns (bytes32 mixedVote) {
        // Chaum mixing: vote' = vote ⊕ H(randomness || mixIndex)
        bytes32 mixHash = keccak256(abi.encodePacked(randomness, mixIndex));
        mixedVote = originalVote ^ mixHash;
        return mixedVote;
    }

    /**
     * @dev Unmixes a vote using the same parameters
     * @param mixedVote The mixed vote
     * @param randomness Random value used in mixing
     * @param mixIndex Index used in mixing
     * @return originalVote The original vote
     */
    function unmixVote(
        bytes32 mixedVote,
        bytes32 randomness,
        uint256 mixIndex
    ) internal pure returns (bytes32 originalVote) {
        // Reverse Chaum mixing: vote = vote' ⊕ H(randomness || mixIndex)
        bytes32 mixHash = keccak256(abi.encodePacked(randomness, mixIndex));
        originalVote = mixedVote ^ mixHash;
        return originalVote;
    }

    // --- Advanced Cryptographic Functions ---

    /**
     * @dev Creates a zero-knowledge proof hash for vote verification
     * @param vote The vote to prove
     * @param nullifier Random value to prevent double-spending
     * @param secret Secret value for proof generation
     * @return proofHash The proof hash
     */
    function createZKProofHash(
        bytes32 vote,
        bytes32 nullifier,
        bytes32 secret
    ) internal pure returns (bytes32 proofHash) {
        proofHash = keccak256(abi.encodePacked(vote, nullifier, secret));
        return proofHash;
    }

    /**
     * @dev Verifies a Merkle proof for inclusion
     * @param leaf The leaf node
     * @param proof The Merkle proof
     * @param root The Merkle root
     * @return isValid Whether the proof is valid
     */
    function verifyMerkleProof(
        bytes32 leaf,
        bytes32[] calldata proof,
        bytes32 root
    ) internal pure returns (bool isValid) {
        return MerkleProof.verify(proof, root, leaf);
    }

    /**
     * @dev Creates a Merkle root from leaves (calldata version)
     * @param leaves Array of leaf nodes
     * @return root The Merkle root
     */
    function createMerkleRoot(bytes32[] calldata leaves) 
        internal 
        pure 
        returns (bytes32 root) 
    {
        require(leaves.length > 0, "Empty leaves array");
        
        if (leaves.length == 1) {
            return leaves[0];
        }
        
        bytes32[] memory currentLevel = new bytes32[]((leaves.length + 1) / 2);
        
        for (uint256 i = 0; i < leaves.length; i += 2) {
            if (i + 1 < leaves.length) {
                currentLevel[i / 2] = keccak256(abi.encodePacked(leaves[i], leaves[i + 1]));
            } else {
                currentLevel[i / 2] = leaves[i];
            }
        }
        
        return createMerkleRootFromLevel(currentLevel);
    }

    /**
     * @dev Creates a Merkle root from leaves (memory version)
     * @param leaves Array of leaf nodes
     * @return root The Merkle root
     */
    function createMerkleRootFromMemory(bytes32[] memory leaves) 
        internal 
        pure 
        returns (bytes32 root) 
    {
        require(leaves.length > 0, "Empty leaves array");
        
        if (leaves.length == 1) {
            return leaves[0];
        }
        
        bytes32[] memory currentLevel = new bytes32[]((leaves.length + 1) / 2);
        
        for (uint256 i = 0; i < leaves.length; i += 2) {
            if (i + 1 < leaves.length) {
                currentLevel[i / 2] = keccak256(abi.encodePacked(leaves[i], leaves[i + 1]));
            } else {
                currentLevel[i / 2] = leaves[i];
            }
        }
        
        return createMerkleRootFromLevel(currentLevel);
    }

    /**
     * @dev Recursive function to build Merkle tree
     * @param level Current level of the tree
     * @return root The Merkle root
     */
    function createMerkleRootFromLevel(bytes32[] memory level) 
        private 
        pure 
        returns (bytes32 root) 
    {
        if (level.length == 1) {
            return level[0];
        }
        
        bytes32[] memory nextLevel = new bytes32[]((level.length + 1) / 2);
        
        for (uint256 i = 0; i < level.length; i += 2) {
            if (i + 1 < level.length) {
                nextLevel[i / 2] = keccak256(abi.encodePacked(level[i], level[i + 1]));
            } else {
                nextLevel[i / 2] = level[i];
            }
        }
        
        return createMerkleRootFromLevel(nextLevel);
    }

    // --- Utility Functions ---

    /**
     * @dev Generates a pseudo-random number based on block data
     * @param seed Additional seed for randomness
     * @return randomNumber The generated random number
     */
    function generateRandomNumber(bytes32 seed) 
        internal 
        view 
        returns (bytes32 randomNumber) 
    {
        return keccak256(abi.encodePacked(
            blockhash(block.number - 1),
            block.timestamp,
            block.prevrandao,
            seed
        ));
    }

    /**
     * @dev Creates a deterministic hash for vote verification
     * @param voter The voter address
     * @param vote The vote value
     * @param nonce The nonce for uniqueness
     * @return voteHash The deterministic vote hash
     */
    function createVoteHash(
        address voter,
        bytes32 vote,
        uint256 nonce
    ) internal pure returns (bytes32 voteHash) {
        voteHash = keccak256(abi.encodePacked(voter, vote, nonce));
        return voteHash;
    }
}

contracts/LiquidDemocracy.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "./libraries/CryptographicUtils.sol";

/**
 * @title LiquidDemocracy
 * @dev Implements liquid democracy with vote delegation capabilities
 * @notice Allows voters to delegate their voting power to trusted representatives
 */
contract LiquidDemocracy is Ownable, ReentrancyGuard {
    using CryptographicUtils for *;

    // --- Structs ---
    struct Voter {
        address voterAddress;
        uint256 votingPower;           // Base voting power
        uint256 delegatedPower;        // Power received from delegations
        uint256 totalPower;            // votingPower + delegatedPower
        address delegate;              // Who this voter delegates to
        address[] delegates;           // Who delegates to this voter
        bool isActive;                 // Whether voter is active
        uint256 lastVoteTime;          // Last time they voted
        uint256 delegationCount;       // Number of active delegations
    }

    struct Delegation {
        address from;
        address to;
        uint256 power;
        uint256 timestamp;
        bool isActive;
        uint256 delegationId;
    }

    struct Vote {
        address voter;
        uint256 candidateId;
        uint256 powerUsed;
        uint256 timestamp;
        bytes32 voteHash;
        bool isDelegated;
    }

    // --- State Variables ---
    mapping(address => Voter) private voters;
    mapping(uint256 => Delegation) private delegations;
    mapping(address => uint256[]) private voterDelegations;
    mapping(uint256 => Vote[]) private candidateVotes;
    mapping(address => mapping(uint256 => bool)) private hasVoted;
    
    address[] private registeredVoters;
    uint256 private delegationCounter;
    uint256 private totalVotingPower;
    uint256 private activeDelegations;
    
    bool public votingActive;
    uint256 public votingStartTime;
    uint256 public votingEndTime;
    uint256 public maxDelegationDepth;
    uint256 public minDelegationPower;
    
    // --- Events ---
    event VoterRegistered(address indexed voter, uint256 power, uint256 timestamp);
    event DelegationCreated(
        address indexed from, 
        address indexed to, 
        uint256 power, 
        uint256 delegationId,
        uint256 timestamp
    );
    event DelegationRevoked(
        address indexed from, 
        address indexed to, 
        uint256 delegationId,
        uint256 timestamp
    );
    event VoteCast(
        address indexed voter, 
        uint256 candidateId, 
        uint256 powerUsed, 
        bool isDelegated,
        uint256 timestamp
    );
    event VotingSessionStarted(uint256 startTime, uint256 endTime);
    event VotingSessionEnded(uint256 endTime);
    event DelegationChainUpdated(address indexed voter, uint256 newTotalPower);

    // --- Modifiers ---
    modifier onlyRegisteredVoter(address voter) {
        require(voters[voter].isActive, "Voter not registered");
        _;
    }

    modifier onlyDuringVoting() {
        require(votingActive, "Voting not active");
        require(block.timestamp >= votingStartTime && block.timestamp <= votingEndTime, "Outside voting period");
        _;
    }

    modifier onlyValidDelegation(address from, address to) {
        require(from != to, "Cannot delegate to self");
        require(voters[from].isActive, "Delegator not registered");
        require(voters[to].isActive, "Delegate not registered");
        require(voters[from].delegate == address(0), "Already has delegate");
        _;
    }

    // --- Constructor ---
    constructor(
        uint256 _maxDelegationDepth,
        uint256 _minDelegationPower
    ) Ownable(msg.sender) {
        maxDelegationDepth = _maxDelegationDepth;
        minDelegationPower = _minDelegationPower;
        delegationCounter = 0;
        activeDelegations = 0;
        totalVotingPower = 0;
    }

    // --- Core Functions ---

    /**
     * @dev Registers a new voter with base voting power
     * @param voter The voter address
     * @param power The base voting power
     */
    function registerVoter(address voter, uint256 power) external onlyOwner {
        require(!voters[voter].isActive, "Voter already registered");
        require(power > 0, "Power must be greater than 0");
        
        Voter memory newVoter = Voter({
            voterAddress: voter,
            votingPower: power,
            delegatedPower: 0,
            totalPower: power,
            delegate: address(0),
            delegates: new address[](0),
            isActive: true,
            lastVoteTime: 0,
            delegationCount: 0
        });
        
        voters[voter] = newVoter;
        registeredVoters.push(voter);
        totalVotingPower += power;
        
        emit VoterRegistered(voter, power, block.timestamp);
    }

    /**
     * @dev Creates a delegation from one voter to another
     * @param delegate The address to delegate to
     * @param power The amount of power to delegate
     * @return delegationId The ID of the created delegation
     */
    function createDelegation(
        address delegate, 
        uint256 power
    ) external 
        onlyRegisteredVoter(msg.sender)
        onlyValidDelegation(msg.sender, delegate)
        returns (uint256 delegationId) 
    {
        require(power >= minDelegationPower, "Power below minimum");
        require(power <= voters[msg.sender].votingPower, "Insufficient power");
        require(!hasDelegationChain(msg.sender, delegate), "Circular delegation detected");
        
        delegationId = delegationCounter++;
        
        Delegation memory newDelegation = Delegation({
            from: msg.sender,
            to: delegate,
            power: power,
            timestamp: block.timestamp,
            isActive: true,
            delegationId: delegationId
        });
        
        delegations[delegationId] = newDelegation;
        voterDelegations[msg.sender].push(delegationId);
        
        // Update voter states
        voters[msg.sender].delegate = delegate;
        voters[msg.sender].votingPower -= power;
        voters[delegate].delegatedPower += power;
        voters[delegate].delegates.push(msg.sender);
        voters[delegate].delegationCount++;
        
        // Update total powers
        updateTotalPower(msg.sender);
        updateTotalPower(delegate);
        
        activeDelegations++;
        
        emit DelegationCreated(msg.sender, delegate, power, delegationId, block.timestamp);
        
        return delegationId;
    }

    /**
     * @dev Revokes an active delegation
     * @param delegationId The ID of the delegation to revoke
     */
    function revokeDelegation(uint256 delegationId) external nonReentrant {
        Delegation storage delegation = delegations[delegationId];
        require(delegation.isActive, "Delegation not active");
        require(delegation.from == msg.sender, "Not the delegator");
        
        delegation.isActive = false;
        
        // Update voter states
        voters[msg.sender].delegate = address(0);
        voters[msg.sender].votingPower += delegation.power;
        voters[delegation.to].delegatedPower -= delegation.power;
        voters[delegation.to].delegationCount--;
        
        // Remove from delegates array
        removeFromDelegates(delegation.to, msg.sender);
        
        // Update total powers
        updateTotalPower(msg.sender);
        updateTotalPower(delegation.to);
        
        activeDelegations--;
        
        emit DelegationRevoked(msg.sender, delegation.to, delegationId, block.timestamp);
    }

    /**
     * @dev Casts a vote using available voting power
     * @param candidateId The candidate to vote for
     * @param power The amount of power to use
     */
    function vote(uint256 candidateId, uint256 power) 
        external 
        onlyRegisteredVoter(msg.sender)
        onlyDuringVoting 
    {
        require(!hasVoted[msg.sender][candidateId], "Already voted for this candidate");
        require(power > 0, "Power must be greater than 0");
        require(power <= voters[msg.sender].totalPower, "Insufficient voting power");
        
        // Create vote
        Vote memory newVote = Vote({
            voter: msg.sender,
            candidateId: candidateId,
            powerUsed: power,
            timestamp: block.timestamp,
            voteHash: CryptographicUtils.createVoteHash(msg.sender, bytes32(candidateId), block.timestamp),
            isDelegated: voters[msg.sender].delegatedPower > 0
        });
        
        candidateVotes[candidateId].push(newVote);
        hasVoted[msg.sender][candidateId] = true;
        voters[msg.sender].lastVoteTime = block.timestamp;
        
        emit VoteCast(msg.sender, candidateId, power, newVote.isDelegated, block.timestamp);
    }

    // --- Admin Functions ---

    /**
     * @dev Starts a voting session
     * @param durationSeconds Duration of the voting session
     */
    function startVotingSession(uint256 durationSeconds) external onlyOwner {
        require(!votingActive, "Voting already active");
        votingActive = true;
        votingStartTime = block.timestamp;
        votingEndTime = block.timestamp + durationSeconds;
        emit VotingSessionStarted(votingStartTime, votingEndTime);
    }

    /**
     * @dev Ends the current voting session
     */
    function endVotingSession() external onlyOwner {
        require(votingActive, "Voting not active");
        votingActive = false;
        votingEndTime = block.timestamp;
        emit VotingSessionEnded(votingEndTime);
    }

    /**
     * @dev Updates delegation parameters
     * @param _maxDelegationDepth New maximum delegation depth
     * @param _minDelegationPower New minimum delegation power
     */
    function updateDelegationParameters(
        uint256 _maxDelegationDepth,
        uint256 _minDelegationPower
    ) external onlyOwner {
        maxDelegationDepth = _maxDelegationDepth;
        minDelegationPower = _minDelegationPower;
    }

    // --- View Functions ---

    /**
     * @dev Gets voter information
     * @param voter The voter address
     * @return The voter structure
     */
    function getVoter(address voter) external view returns (Voter memory) {
        return voters[voter];
    }

    /**
     * @dev Gets delegation information
     * @param delegationId The delegation ID
     * @return The delegation structure
     */
    function getDelegation(uint256 delegationId) external view returns (Delegation memory) {
        return delegations[delegationId];
    }

    /**
     * @dev Gets all votes for a candidate
     * @param candidateId The candidate ID
     * @return Array of votes
     */
    function getCandidateVotes(uint256 candidateId) external view returns (Vote[] memory) {
        return candidateVotes[candidateId];
    }

    /**
     * @dev Gets total votes for a candidate
     * @param candidateId The candidate ID
     * @return Total voting power used
     */
    function getCandidateTotalVotes(uint256 candidateId) external view returns (uint256) {
        uint256 total = 0;
        for (uint256 i = 0; i < candidateVotes[candidateId].length; i++) {
            total += candidateVotes[candidateId][i].powerUsed;
        }
        return total;
    }

    /**
     * @dev Gets all registered voters
     * @return Array of voter addresses
     */
    function getRegisteredVoters() external view returns (address[] memory) {
        return registeredVoters;
    }

    /**
     * @dev Gets delegations for a voter
     * @param voter The voter address
     * @return Array of delegation IDs
     */
    function getVoterDelegations(address voter) external view returns (uint256[] memory) {
        return voterDelegations[voter];
    }

    /**
     * @dev Checks if a voter has voted for a candidate
     * @param voter The voter address
     * @param candidateId The candidate ID
     * @return Whether the voter has voted
     */
    function hasVoterVoted(address voter, uint256 candidateId) external view returns (bool) {
        return hasVoted[voter][candidateId];
    }

    /**
     * @dev Gets total voting power in the system
     * @return Total voting power
     */
    function getTotalVotingPower() external view returns (uint256) {
        return totalVotingPower;
    }

    /**
     * @dev Gets active delegations count
     * @return Number of active delegations
     */
    function getActiveDelegations() external view returns (uint256) {
        return activeDelegations;
    }

    // --- Internal Functions ---

    /**
     * @dev Updates total power for a voter
     * @param voter The voter address
     */
    function updateTotalPower(address voter) internal {
        voters[voter].totalPower = voters[voter].votingPower + voters[voter].delegatedPower;
        emit DelegationChainUpdated(voter, voters[voter].totalPower);
    }

    /**
     * @dev Checks for circular delegation chains
     * @param from Starting address
     * @param to Target address
     * @return Whether a circular delegation exists
     */
    function hasDelegationChain(address from, address to) internal view returns (bool) {
        if (from == to) return true;
        
        address current = to;
        uint256 depth = 0;
        
        while (voters[current].delegate != address(0) && depth < maxDelegationDepth) {
            current = voters[current].delegate;
            if (current == from) return true;
            depth++;
        }
        
        return false;
    }

    /**
     * @dev Removes a delegate from the delegates array
     * @param voter The voter address
     * @param delegate The delegate to remove
     */
    function removeFromDelegates(address voter, address delegate) internal {
        address[] storage delegates = voters[voter].delegates;
        for (uint256 i = 0; i < delegates.length; i++) {
            if (delegates[i] == delegate) {
                delegates[i] = delegates[delegates.length - 1];
                delegates.pop();
                break;
            }
        }
    }
}

