// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.28;

// Uncomment this line to use console.log
// import "hardhat/console.sol";

contract Lock {
    uint public unlockTime;
    address payable public owner;

    event Withdrawal(uint amount, uint when);

    constructor(uint _unlockTime) payable {
        require(
            block.timestamp < _unlockTime,
            "Unlock time should be in the future"
        );

        unlockTime = _unlockTime;
        owner = payable(msg.sender);
    }

    function withdraw() public {
        // Uncomment this line, and the import of "hardhat/console.sol", to print a log in your terminal
        // console.log("Unlock time is %o and block timestamp is %o", unlockTime, block.timestamp);

        require(block.timestamp >= unlockTime, "You can't withdraw yet");
        require(msg.sender == owner, "You aren't the owner");

        emit Withdrawal(address(this).balance, block.timestamp);

        owner.transfer(address(this).balance);
    }
}

contracts/VoteMixing.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "./libraries/CryptographicUtils.sol";

/**
 * @title VoteMixing
 * @dev Implements Chaum mixing for vote anonymity
 * @notice Provides vote mixing capabilities to enhance voter privacy
 */
contract VoteMixing is Ownable, ReentrancyGuard {
    using CryptographicUtils for *;

    // --- Structs ---
    struct MixedVote {
        bytes32 originalVote;
        bytes32 mixedVote;
        bytes32 randomness;
        uint256 mixIndex;
        uint256 timestamp;
        bool isRevealed;
        address voter;
    }

    struct MixingRound {
        uint256 roundId;
        bytes32[] mixedVotes;
        bytes32[] randomness;
        uint256[] mixIndices;
        uint256 startTime;
        uint256 endTime;
        uint256 revealTime;
        bool isActive;
        bool isRevealed;
        uint256 participantCount;
        bytes32 merkleRoot;
    }

    struct MixingProof {
        bytes32 originalVote;
        bytes32 mixedVote;
        bytes32 randomness;
        uint256 mixIndex;
        bytes32[] merkleProof;
    }

    // --- State Variables ---
    mapping(uint256 => MixingRound) private mixingRounds;
    mapping(bytes32 => MixedVote) private mixedVotes;
    mapping(address => uint256[]) private voterRounds;
    mapping(uint256 => address[]) private roundParticipants;
    mapping(bytes32 => bool) private usedRandomness;
    
    uint256 private roundCounter;
    uint256 private minParticipants;
    uint256 private maxParticipants;
    uint256 private mixingDuration;
    uint256 private revealDelay;
    
    bool public mixingEnabled;
    
    // --- Events ---
    event MixingRoundCreated(
        uint256 indexed roundId,
        uint256 startTime,
        uint256 endTime,
        uint256 minParticipants
    );
    
    event VoteSubmitted(
        uint256 indexed roundId,
        address indexed voter,
        bytes32 mixedVote,
        uint256 mixIndex,
        uint256 timestamp
    );
    
    event MixingRoundCompleted(
        uint256 indexed roundId,
        uint256 participantCount,
        bytes32 merkleRoot,
        uint256 timestamp
    );
    
    event VoteRevealed(
        uint256 indexed roundId,
        address indexed voter,
        bytes32 originalVote,
        bytes32 mixedVote,
        uint256 timestamp
    );
    
    event MixingRoundRevealed(
        uint256 indexed roundId,
        uint256 totalVotes,
        uint256 timestamp
    );

    // --- Modifiers ---
    modifier onlyActiveRound(uint256 roundId) {
        require(mixingRounds[roundId].isActive, "Round not active");
        _;
    }

    modifier onlyRevealTime(uint256 roundId) {
        require(block.timestamp >= mixingRounds[roundId].revealTime, "Reveal time not reached");
        _;
    }

    modifier onlyValidRandomness(bytes32 randomness) {
        require(!usedRandomness[randomness], "Randomness already used");
        _;
    }

    // --- Constructor ---
    constructor(
        uint256 _minParticipants,
        uint256 _maxParticipants,
        uint256 _mixingDuration,
        uint256 _revealDelay
    ) Ownable(msg.sender) {
        minParticipants = _minParticipants;
        maxParticipants = _maxParticipants;
        mixingDuration = _mixingDuration;
        revealDelay = _revealDelay;
        roundCounter = 0;
        mixingEnabled = true;
    }

    // --- Core Functions ---

    /**
     * @dev Creates a new mixing round
     * @return roundId The ID of the created round
     */
    function createMixingRound() external onlyOwner returns (uint256 roundId) {
        require(mixingEnabled, "Mixing disabled");
        
        roundId = roundCounter++;
        
        MixingRound memory newRound = MixingRound({
            roundId: roundId,
            mixedVotes: new bytes32[](0),
            randomness: new bytes32[](0),
            mixIndices: new uint256[](0),
            startTime: block.timestamp,
            endTime: block.timestamp + mixingDuration,
            revealTime: block.timestamp + mixingDuration + revealDelay,
            isActive: true,
            isRevealed: false,
            participantCount: 0,
            merkleRoot: bytes32(0)
        });
        
        mixingRounds[roundId] = newRound;
        
        emit MixingRoundCreated(roundId, newRound.startTime, newRound.endTime, minParticipants);
        
        return roundId;
    }

    /**
     * @dev Submits a vote to the mixing round
     * @param roundId The mixing round ID
     * @param originalVote The original vote to mix
     * @param randomness Random value for mixing
     * @param mixIndex Index for mixing
     * @return mixedVote The mixed vote
     */
    function submitVote(
        uint256 roundId,
        bytes32 originalVote,
        bytes32 randomness,
        uint256 mixIndex
    ) external 
        onlyActiveRound(roundId)
        onlyValidRandomness(randomness)
        returns (bytes32 mixedVote) 
    {
        MixingRound storage round = mixingRounds[roundId];
        require(block.timestamp <= round.endTime, "Round ended");
        require(round.participantCount < maxParticipants, "Round full");
        
        // Check if voter already participated
        require(!hasVoterParticipated(roundId, msg.sender), "Already participated");
        
        // Create mixed vote
        mixedVote = CryptographicUtils.mixVote(originalVote, randomness, mixIndex);
        
        // Store mixed vote
        MixedVote memory newMixedVote = MixedVote({
            originalVote: originalVote,
            mixedVote: mixedVote,
            randomness: randomness,
            mixIndex: mixIndex,
            timestamp: block.timestamp,
            isRevealed: false,
            voter: msg.sender
        });
        
        mixedVotes[mixedVote] = newMixedVote;
        
        // Add to round
        round.mixedVotes.push(mixedVote);
        round.randomness.push(randomness);
        round.mixIndices.push(mixIndex);
        round.participantCount++;
        roundParticipants[roundId].push(msg.sender);
        voterRounds[msg.sender].push(roundId);
        
        usedRandomness[randomness] = true;
        
        emit VoteSubmitted(roundId, msg.sender, mixedVote, mixIndex, block.timestamp);
        
        return mixedVote;
    }

    /**
     * @dev Completes a mixing round and creates Merkle tree
     * @param roundId The mixing round ID
     */
    function completeMixingRound(uint256 roundId) external onlyOwner {
        MixingRound storage round = mixingRounds[roundId];
        require(round.isActive, "Round not active");
        require(block.timestamp >= round.endTime, "Round not ended");
        require(round.participantCount >= minParticipants, "Insufficient participants");
        
        round.isActive = false;
        
        // Create Merkle root from mixed votes
        // Convert storage array to memory for Merkle root calculation
        bytes32[] memory mixedVotes = new bytes32[](round.mixedVotes.length);
        for (uint256 i = 0; i < round.mixedVotes.length; i++) {
            mixedVotes[i] = round.mixedVotes[i];
        }
        round.merkleRoot = CryptographicUtils.createMerkleRootFromMemory(mixedVotes);
        
        emit MixingRoundCompleted(roundId, round.participantCount, round.merkleRoot, block.timestamp);
    }

    /**
     * @dev Reveals a vote after mixing round completion
     * @param roundId The mixing round ID
     * @param mixedVote The mixed vote to reveal
     * @param originalVote The original vote
     * @param randomness The randomness used
     * @param mixIndex The mix index used
     */
    function revealVote(
        uint256 roundId,
        bytes32 mixedVote,
        bytes32 originalVote,
        bytes32 randomness,
        uint256 mixIndex
    ) external onlyRevealTime(roundId) {
        MixedVote storage vote = mixedVotes[mixedVote];
        require(vote.voter == msg.sender, "Not your vote");
        require(!vote.isRevealed, "Vote already revealed");
        
        // Verify the mixing
        bytes32 computedMixedVote = CryptographicUtils.mixVote(originalVote, randomness, mixIndex);
        require(computedMixedVote == mixedVote, "Invalid reveal");
        
        vote.isRevealed = true;
        
        emit VoteRevealed(roundId, msg.sender, originalVote, mixedVote, block.timestamp);
    }

    /**
     * @dev Reveals all votes in a mixing round
     * @param roundId The mixing round ID
     * @return revealedCount Number of votes revealed
     */
    function revealMixingRound(uint256 roundId) 
        external 
        onlyOwner 
        onlyRevealTime(roundId) 
        returns (uint256 revealedCount) 
    {
        MixingRound storage round = mixingRounds[roundId];
        require(!round.isRevealed, "Round already revealed");
        
        round.isRevealed = true;
        revealedCount = round.participantCount;
        
        emit MixingRoundRevealed(roundId, revealedCount, block.timestamp);
        
        return revealedCount;
    }

    // --- View Functions ---

    /**
     * @dev Gets mixing round information
     * @param roundId The round ID
     * @return The mixing round structure
     */
    function getMixingRound(uint256 roundId) external view returns (MixingRound memory) {
        return mixingRounds[roundId];
    }

    /**
     * @dev Gets mixed vote information
     * @param mixedVote The mixed vote hash
     * @return The mixed vote structure
     */
    function getMixedVote(bytes32 mixedVote) external view returns (MixedVote memory) {
        return mixedVotes[mixedVote];
    }

    /**
     * @dev Gets all participants in a round
     * @param roundId The round ID
     * @return Array of participant addresses
     */
    function getRoundParticipants(uint256 roundId) external view returns (address[] memory) {
        return roundParticipants[roundId];
    }

    /**
     * @dev Gets all rounds a voter participated in
     * @param voter The voter address
     * @return Array of round IDs
     */
    function getVoterRounds(address voter) external view returns (uint256[] memory) {
        return voterRounds[voter];
    }

    /**
     * @dev Gets all mixed votes in a round
     * @param roundId The round ID
     * @return Array of mixed vote hashes
     */
    function getRoundMixedVotes(uint256 roundId) external view returns (bytes32[] memory) {
        return mixingRounds[roundId].mixedVotes;
    }

    /**
     * @dev Verifies a vote's inclusion in the mixing round
     * @param roundId The round ID
     * @param mixedVote The mixed vote
     * @param proof The Merkle proof
     * @return Whether the vote is included
     */
    function verifyVoteInclusion(
        uint256 roundId,
        bytes32 mixedVote,
        bytes32[] calldata proof
    ) external view returns (bool) {
        return CryptographicUtils.verifyMerkleProof(
            mixedVote, 
            proof, 
            mixingRounds[roundId].merkleRoot
        );
    }

    /**
     * @dev Checks if a voter participated in a round
     * @param roundId The round ID
     * @param voter The voter address
     * @return Whether the voter participated
     */
    function hasVoterParticipated(uint256 roundId, address voter) public view returns (bool) {
        address[] memory participants = roundParticipants[roundId];
        for (uint256 i = 0; i < participants.length; i++) {
            if (participants[i] == voter) {
                return true;
            }
        }
        return false;
    }

    /**
     * @dev Gets mixing parameters
     * @return minParticipants Minimum participants required
     * @return maxParticipants Maximum participants allowed
     * @return mixingDuration Duration of mixing phase
     * @return revealDelay Delay before reveal phase
     */
    function getMixingParameters() external view returns (
        uint256 minParticipants,
        uint256 maxParticipants,
        uint256 mixingDuration,
        uint256 revealDelay
    ) {
        return (minParticipants, maxParticipants, mixingDuration, revealDelay);
    }

    /**
     * @dev Gets total number of rounds
     * @return Total round count
     */
    function getTotalRounds() external view returns (uint256) {
        return roundCounter;
    }

    // --- Admin Functions ---

    /**
     * @dev Updates mixing parameters
     * @param _minParticipants New minimum participants
     * @param _maxParticipants New maximum participants
     * @param _mixingDuration New mixing duration
     * @param _revealDelay New reveal delay
     */
    function updateMixingParameters(
        uint256 _minParticipants,
        uint256 _maxParticipants,
        uint256 _mixingDuration,
        uint256 _revealDelay
    ) external onlyOwner {
        minParticipants = _minParticipants;
        maxParticipants = _maxParticipants;
        mixingDuration = _mixingDuration;
        revealDelay = _revealDelay;
    }

    /**
     * @dev Enables or disables mixing
     * @param enabled Whether mixing should be enabled
     */
    function setMixingEnabled(bool enabled) external onlyOwner {
        mixingEnabled = enabled;
    }

    /**
     * @dev Emergency function to invalidate a mixing round
     * @param roundId The round ID to invalidate
     */
    function emergencyInvalidateRound(uint256 roundId) external onlyOwner {
        require(mixingRounds[roundId].isActive, "Round not active");
        mixingRounds[roundId].isActive = false;
    }

    // --- Utility Functions ---

    /**
     * @dev Generates a random mixing index
     * @param seed Additional seed for randomness
     * @return mixIndex The generated mix index
     */
    function generateMixIndex(bytes32 seed) external view returns (uint256 mixIndex) {
        bytes32 random = CryptographicUtils.generateRandomNumber(seed);
        return uint256(random) % maxParticipants;
    }

    /**
     * @dev Creates a mixing proof for verification
     * @param originalVote The original vote
     * @param mixedVote The mixed vote
     * @param randomness The randomness used
     * @param mixIndex The mix index used
     * @return proof The mixing proof structure
     */
    function createMixingProof(
        bytes32 originalVote,
        bytes32 mixedVote,
        bytes32 randomness,
        uint256 mixIndex
    ) external pure returns (MixingProof memory proof) {
        proof = MixingProof({
            originalVote: originalVote,
            mixedVote: mixedVote,
            randomness: randomness,
            mixIndex: mixIndex,
            merkleProof: new bytes32[](0)
        });
        return proof;
    }
}

contracts/ZKProofVerifier.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "./libraries/CryptographicUtils.sol";

/**
 * @title ZKProofVerifier
 * @dev Zero-Knowledge Proof verification for vote privacy and verifiability
 * @notice Implements zk-SNARKs principles for secure voting
 */
contract ZKProofVerifier is Ownable {
    using CryptographicUtils for *;

    // --- Structs ---
    struct ZKProof {
        bytes32 publicInputs;      // Public inputs to the proof
        bytes32 nullifierHash;     // Hash to prevent double-spending
        bytes32 commitmentHash;    // Commitment to the vote
        bytes32 proofHash;         // Hash of the zero-knowledge proof
        uint256 timestamp;         // When the proof was created
        bool verified;             // Whether the proof has been verified
    }

    struct VoteProof {
        address voter;             // Voter address (hidden in ZK)
        bytes32 voteHash;          // Hash of the actual vote
        bytes32 nullifier;         // Unique nullifier
        bytes32 secret;            // Secret for proof generation
        ZKProof zkProof;          // Zero-knowledge proof
    }

    // --- State Variables ---
    mapping(bytes32 => ZKProof) private proofs;
    mapping(bytes32 => bool) private nullifiers;
    mapping(address => bytes32[]) private voterProofs;
    mapping(bytes32 => VoteProof) private voteProofs;
    
    uint256 public proofCounter;
    bytes32 public merkleRoot;
    uint256 public verificationThreshold;
    
    // --- Events ---
    event ZKProofCreated(
        bytes32 indexed proofHash,
        bytes32 indexed nullifierHash,
        bytes32 commitmentHash,
        uint256 timestamp
    );
    
    event ZKProofVerified(
        bytes32 indexed proofHash,
        bool isValid,
        uint256 timestamp
    );
    
    event VoteProofSubmitted(
        address indexed voter,
        bytes32 indexed voteHash,
        bytes32 proofHash,
        uint256 timestamp
    );
    
    event NullifierUsed(
        bytes32 indexed nullifier,
        bytes32 indexed proofHash,
        uint256 timestamp
    );

    // --- Modifiers ---
    modifier onlyValidProof(bytes32 proofHash) {
        require(proofs[proofHash].proofHash != bytes32(0), "Proof does not exist");
        _;
    }

    modifier onlyUnusedNullifier(bytes32 nullifier) {
        require(!nullifiers[nullifier], "Nullifier already used");
        _;
    }

    // --- Constructor ---
    constructor(uint256 _verificationThreshold) Ownable(msg.sender) {
        verificationThreshold = _verificationThreshold;
        proofCounter = 0;
    }

    // --- Core ZK Proof Functions ---

    /**
     * @dev Creates a zero-knowledge proof for vote verification
     * @param voteHash Hash of the vote to prove
     * @param nullifier Unique nullifier to prevent double-spending
     * @param secret Secret value for proof generation
     * @param commitmentHash Commitment to the vote
     * @return proofHash The created proof hash
     */
    function createZKProof(
        bytes32 voteHash,
        bytes32 nullifier,
        bytes32 secret,
        bytes32 commitmentHash
    ) external onlyUnusedNullifier(nullifier) returns (bytes32 proofHash) {
        // Create ZK proof hash
        proofHash = CryptographicUtils.createZKProofHash(voteHash, nullifier, secret);
        
        // Create ZK proof structure
        ZKProof memory newProof = ZKProof({
            publicInputs: commitmentHash,
            nullifierHash: keccak256(abi.encodePacked(nullifier)),
            commitmentHash: commitmentHash,
            proofHash: proofHash,
            timestamp: block.timestamp,
            verified: false
        });
        
        // Store the proof
        proofs[proofHash] = newProof;
        nullifiers[nullifier] = true;
        proofCounter++;
        
        emit ZKProofCreated(proofHash, newProof.nullifierHash, commitmentHash, block.timestamp);
        emit NullifierUsed(nullifier, proofHash, block.timestamp);
        
        return proofHash;
    }

    /**
     * @dev Verifies a zero-knowledge proof
     * @param proofHash The proof hash to verify
     * @param voteHash The vote hash being proved
     * @param nullifier The nullifier used in the proof
     * @param secret The secret used in the proof
     * @return isValid Whether the proof is valid
     */
    function verifyZKProof(
        bytes32 proofHash,
        bytes32 voteHash,
        bytes32 nullifier,
        bytes32 secret
    ) external onlyValidProof(proofHash) returns (bool isValid) {
        ZKProof storage proof = proofs[proofHash];
        
        // Verify the proof hasn't been used before
        require(!proof.verified, "Proof already verified");
        
        // Verify the nullifier hasn't been used
        require(nullifiers[nullifier], "Nullifier not found");
        
        // Recreate the proof hash to verify
        bytes32 computedProofHash = CryptographicUtils.createZKProofHash(voteHash, nullifier, secret);
        
        // Verify the proof hash matches
        isValid = (computedProofHash == proofHash);
        
        if (isValid) {
            proof.verified = true;
            emit ZKProofVerified(proofHash, true, block.timestamp);
        } else {
            emit ZKProofVerified(proofHash, false, block.timestamp);
        }
        
        return isValid;
    }

    /**
     * @dev Submits a complete vote proof with ZK verification
     * @param voteHash Hash of the vote
     * @param nullifier Unique nullifier
     * @param secret Secret for proof generation
     * @param commitmentHash Commitment to the vote
     * @return proofHash The created proof hash
     */
    function submitVoteProof(
        bytes32 voteHash,
        bytes32 nullifier,
        bytes32 secret,
        bytes32 commitmentHash
    ) external returns (bytes32 proofHash) {
        // Create the ZK proof
        proofHash = this.createZKProof(voteHash, nullifier, secret, commitmentHash);
        
        // Create vote proof structure
        VoteProof memory voteProof = VoteProof({
            voter: msg.sender,
            voteHash: voteHash,
            nullifier: nullifier,
            secret: secret,
            zkProof: proofs[proofHash]
        });
        
        // Store the vote proof
        voteProofs[proofHash] = voteProof;
        voterProofs[msg.sender].push(proofHash);
        
        emit VoteProofSubmitted(msg.sender, voteHash, proofHash, block.timestamp);
        
        return proofHash;
    }

    // --- Merkle Tree Functions ---

    /**
     * @dev Updates the Merkle root for voter verification
     * @param newRoot The new Merkle root
     */
    function updateMerkleRoot(bytes32 newRoot) external onlyOwner {
        merkleRoot = newRoot;
    }

    /**
     * @dev Verifies a voter's inclusion in the Merkle tree
     * @param voter The voter address
     * @param proof The Merkle proof
     * @return isValid Whether the voter is included
     */
    function verifyVoterInclusion(
        address voter,
        bytes32[] calldata proof
    ) external view returns (bool isValid) {
        bytes32 leaf = keccak256(abi.encodePacked(voter));
        return CryptographicUtils.verifyMerkleProof(leaf, proof, merkleRoot);
    }

    // --- View Functions ---

    /**
     * @dev Gets a ZK proof by hash
     * @param proofHash The proof hash
     * @return The ZK proof structure
     */
    function getZKProof(bytes32 proofHash) 
        external 
        view 
        onlyValidProof(proofHash) 
        returns (ZKProof memory) 
    {
        return proofs[proofHash];
    }

    /**
     * @dev Gets a vote proof by hash
     * @param proofHash The proof hash
     * @return The vote proof structure
     */
    function getVoteProof(bytes32 proofHash) 
        external 
        view 
        returns (VoteProof memory) 
    {
        return voteProofs[proofHash];
    }

    /**
     * @dev Gets all proofs for a voter
     * @param voter The voter address
     * @return Array of proof hashes
     */
    function getVoterProofs(address voter) 
        external 
        view 
        returns (bytes32[] memory) 
    {
        return voterProofs[voter];
    }

    /**
     * @dev Checks if a nullifier has been used
     * @param nullifier The nullifier to check
     * @return Whether the nullifier has been used
     */
    function isNullifierUsed(bytes32 nullifier) external view returns (bool) {
        return nullifiers[nullifier];
    }

    /**
     * @dev Gets the total number of proofs created
     * @return The proof counter
     */
    function getProofCount() external view returns (uint256) {
        return proofCounter;
    }

    /**
     * @dev Gets the current Merkle root
     * @return The Merkle root
     */
    function getMerkleRoot() external view returns (bytes32) {
        return merkleRoot;
    }

    // --- Admin Functions ---

    /**
     * @dev Updates the verification threshold
     * @param newThreshold The new threshold value
     */
    function updateVerificationThreshold(uint256 newThreshold) external onlyOwner {
        verificationThreshold = newThreshold;
    }

    /**
     * @dev Batch verifies multiple proofs
     * @param proofHashes Array of proof hashes to verify
     * @param voteHashes Array of vote hashes
     * @param nullifiers Array of nullifiers
     * @param secrets Array of secrets
     * @return validCount Number of valid proofs
     */
    function batchVerifyProofs(
        bytes32[] calldata proofHashes,
        bytes32[] calldata voteHashes,
        bytes32[] calldata nullifiers,
        bytes32[] calldata secrets
    ) external onlyOwner returns (uint256 validCount) {
        require(
            proofHashes.length == voteHashes.length &&
            voteHashes.length == nullifiers.length &&
            nullifiers.length == secrets.length,
            "Array lengths must match"
        );
        
        for (uint256 i = 0; i < proofHashes.length; i++) {
            if (this.verifyZKProof(proofHashes[i], voteHashes[i], nullifiers[i], secrets[i])) {
                validCount++;
            }
        }
        
        return validCount;
    }

    // --- Emergency Functions ---

    /**
     * @dev Emergency function to invalidate a proof (admin only)
     * @param proofHash The proof hash to invalidate
     */
    function emergencyInvalidateProof(bytes32 proofHash) external onlyOwner {
        require(proofs[proofHash].proofHash != bytes32(0), "Proof does not exist");
        delete proofs[proofHash];
    }
}

